import axios, { AxiosResponse } from "axios";
import { table } from "console";
import { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import Button from "utils/Button";
import { urlGeneros } from "utils/endpoints";
import ListadoGenerico from "utils/ListadoGenerico";
import Paginacion from "utils/Paginacion";
import { generoDTO } from "./generos.model";

export default function IndiceGeneros() {

    const [generos, setGeneros] = useState<generoDTO[]>();
    const [totalDePaginas, setTotalDePaginas] = useState(1);
    const [recordsPorPagina, setRecordsPorPagina] = useState(10);
    const [pagina, setPagina] = useState(1);

    useEffect(() => {
        console.log(urlGeneros)
        axios.get(urlGeneros, {
            params: {pagina: pagina, recordsPorPagina: recordsPorPagina}
        })
        .then((respuesta: AxiosResponse<generoDTO[]>) => {
            const totalDeRegistros = 
                parseInt(respuesta.headers['cantidadTotalRegistros'], 10);
            setTotalDePaginas(Math.ceil(totalDeRegistros/recordsPorPagina))
            console.log(totalDePaginas);
            console.log(respuesta.data);
            setGeneros(respuesta.data);
        })
    }, [pagina, recordsPorPagina])

    return (
        <>
            <h3>Géneros</h3>
            <Link className="btn btn-primary" to="generos/crear">Crear Género</Link>

            <select 
                className="form-control"
                onChange={e => {
                    setPagina(1);
                    setRecordsPorPagina(parseInt(e.currentTarget.value, 10))
                    }
                }>
                <option value={5}></option>
                <option value={10}></option>
                <option value={25}></option>
                <option value={50}></option>
            </select>
            <Paginacion 
                paginaActual={pagina}
                onChange={nuevaPagina => setPagina(nuevaPagina)}
                cantidadTotalDePaginas={totalDePaginas}
            ></Paginacion>

            <ListadoGenerico listado={generos}>
                <table className="table table-striped">
                    <thead>
                        <th></th>
                        <th>Nombre</th>
                    </thead>
                    <tbody>
                        {generos?.map(genero => 
                            <tr key={genero.id}>
                                <td>
                                    <Link className="btn btn-success" to={`/generos/${genero.id}`}>Editar</Link>
                                    <Button className="btn btn-danger">Borrar</Button>
                                </td>
                                <td>
                                    {genero.nombre}
                                </td>
                            </tr>
                        )}
                    </tbody>
                </table>
            </ListadoGenerico>


        </>

    )
}
