import axios, { AxiosResponse } from "axios";
import { table } from "console";
import { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import Button from "utils/Button";
import { urlGeneros } from "utils/endpoints";
import ListadoGenerico from "utils/ListadoGenerico";
import Paginacion from "utils/Paginacion";
import { number } from "yup/lib/locale";
import { generoDTO } from "./generos.model";
import confirmar from "utils/Confirmar";

export default function IndiceGeneros() {

    const [generos, setGeneros] = useState<generoDTO[]>();
    const [totalDePaginas, setTotalDePaginas] = useState(0);
    const [recordsPorPagina, setRecordsPorPagina] = useState(10);
    const [pagina, setPagina] = useState(1);

    useEffect(() => {
        cargarDatos();
    }, [pagina, recordsPorPagina])

    function cargarDatos(){
        axios.get(urlGeneros, {
            params: {pagina, recordsPorPagina}
        })
        .then((respuesta: AxiosResponse<generoDTO[]>) => {
            const totalDeRegistros = 
                parseInt(respuesta.headers['cantidadtotalregistros'], 10);
            setTotalDePaginas(Math.ceil(totalDeRegistros/recordsPorPagina))
            console.log(totalDePaginas);
            console.log(respuesta.data);
            setGeneros(respuesta.data);
        })
    }

    async function borrar(id:number) {
        try {
            await axios.delete(urlGeneros + "/" + id);
            cargarDatos();
        }catch(error) {
            console.log(error.response.data)
        }
    }

    return (
        <>
            <h3>Géneros</h3>
            <Link className="btn btn-primary" to="generos/crear">Crear Género</Link>

            <select 
                className="form-control"
                onChange={e => {
                    setPagina(1);           
                    setRecordsPorPagina(Number(e.currentTarget.value))
                    }
                }>
                <option value={5}>5</option>
                <option value={10}>10</option>
                <option value={25}>25</option>
                <option value={50}>50</option>
            </select>


            <ListadoGenerico listado={generos}>
                <table className="table table-striped">
                    <thead>
                        <th></th>
                        <th>Nombre</th>
                    </thead>
                    <tbody>
                        {generos?.map(genero => 
                            <tr key={genero.id}>
                                <td>
                                    <Link className="btn btn-success mx-2" to={`/generos/editar/${genero.id}`}>Editar</Link>
                                    <Button
                                        onClick={() => confirmar(() => borrar(genero.id))}
                                        className="btn btn-danger mx-2"
                                        >Borrar</Button>
                                </td>
                                <td>
                                    {genero.nombre}
                                </td>
                            </tr>
                        )}
                    </tbody>
                </table>
            </ListadoGenerico>


            <Paginacion 
                paginaActual={pagina}
                onChange={nuevaPagina => setPagina(nuevaPagina)}
                cantidadTotalDePaginas={totalDePaginas}
            ></Paginacion>


        </>

    )
}
