import axios from "axios";
import EditarEntidad from "utils/EditarEntidad";
import { urlPromocion } from "utils/endpoints";
import FormularioPromociones from "./FormularioPromociones";
import { promocionCreacionDTO, promocionDTO } from "./promociones.model";



export default function EditarPromocion() {

    async function EditarPromocion(peliculaEditar:peliculaCreacionDTO) {
        try{
            const formData = convertirPeliculaAFormData(peliculaEditar);
            await axios({
                method: 'put',
                url: `${urlPeliculas}/${id}`,
                data: formData,
                headers: {'Content-Type': 'multipart/form-data'}
            }).then((respuesta: AxiosResponse<number>) => {
                history.push(`/pelicula/${id}`);
            });
        }catch(error){
            setErrores(error.response.data);
        }
    }

    return (
        <>
            <EditarEntidad<promocionCreacionDTO, promocionDTO>
                url={urlPromocion} urlIndice="/promociones" nombreEntidad="Promociones"
            >
                {(entidad, editar) => 
                    <FormularioPromociones modelo={entidad} 
                    onSubmit={async valores => {
                       await EditarPromocion(valores);
                    }}
                    /> 
                
                }
            </EditarEntidad>
        </>

    )
}
