import axios, { AxiosResponse } from "axios";
import { useEffect, useState } from "react";
import { useHistory, useParams } from "react-router-dom";
import EditarEntidad from "utils/EditarEntidad";
import { urlPromocion } from "utils/endpoints";
import FormularioPromociones from "./FormularioPromociones";
import { promocionCreacionDTO, promocionDTO } from "./promociones.model";



export default function EditarPromocion() {

    const [promociones, setPromociones] = useState<promocionCreacionDTO>();
    const [promocionesPutGet, setPromocionesPutGet] = useState<promocionDTO>();
    const {id}: any = useParams();

    const history = useHistory();
    const [errores, setErrores] = useState<string[]>([]);

    useEffect(() => {
        axios.get(`${urlPeliculas}/PutGet/${id}`)
        .then((respuesta: AxiosResponse<promocionDTO>) => {
            const modelo: promocionCreacionDTO = {
                titulo: respuesta.data.pelicula.titulo,
                enCines: respuesta.data.pelicula.enCines,
                trailer: respuesta.data.pelicula.trailer,
                posterURL: respuesta.data.pelicula.poster,
                resumen: respuesta.data.pelicula.resumen,
                fechaLanzamiento: new Date(respuesta.data.pelicula.fechaLanzamiento)
            };
            setPromociones(modelo);
            setPromocionesPutGet(respuesta.data);
        })
    }, [id])

    async function EditarPromocion(peliculaEditar:peliculaCreacionDTO) {
        try{
            const formData = convertirPeliculaAFormData(peliculaEditar);
            await axios({
                method: 'put',
                url: `${urlPeliculas}/${id}`,
                data: formData,
                headers: {'Content-Type': 'multipart/form-data'}
            }).then((respuesta: AxiosResponse<number>) => {
                history.push(`/pelicula/${id}`);
            });
        }catch(error){
            setErrores(error.response.data);
        }
    }

    return (
        <>
            <EditarEntidad<promocionCreacionDTO, promocionDTO>
                url={urlPromocion} urlIndice="/promociones" nombreEntidad="Promociones"
            >
                {(entidad) => 
                    <FormularioPromociones 
                    modelo={entidad} 
                    onSubmit={async valores => {
                       await EditarPromocion(valores);
                    }}
                    /> 
                
                }
            </EditarEntidad>
        </>

    )
}
