import { useState, useEffect } from "react";
import { landingPageDTO } from './peliculas/peliculas.model'
import ListadoPeliculas from './peliculas/ListadoPeliculas'
import axios, { AxiosResponse } from "axios";
import { urlPeliculas } from "utils/endpoints";
import AlertaConext from "utils/AlertaConext";
import Autorizado from "auth/Autorizado";


export default function LandingPage() {

    const [peliculas, setPeliculas] = useState<landingPageDTO>({})

    useEffect(() => {
        cargarDatos();
    }, [])

    function cargarDatos () {
        axios.get(urlPeliculas)
        .then((respuesta: AxiosResponse<landingPageDTO>) => {
            setPeliculas(respuesta.data);
        })
    }

    return (
        <>
            <Autorizado 
                autorizado={<>Estas autorizado</>}
                noAutorizado={<>NOOO estas autorizado</>}
             />

            <AlertaConext.Provider value={() => cargarDatos()} >
            <h3>En Cartelera</h3>
            <ListadoPeliculas peliculas={peliculas.enCines} />

            <h3>Pr√≥ximos Estrenos</h3>
            <ListadoPeliculas peliculas={peliculas.proximosEstrenos} />

            </AlertaConext.Provider>

        </>
    )
}
