import axios, { AxiosResponse } from "axios";
import { useEffect, useState } from "react";
import { urlViajes } from "utils/endpoints";
import { viajeDescuentoDTO } from "viajes/viajes.model";

export default function OfertasIndex() {

    const [ofertas, setOfertas] = useState<viajeDescuentoDTO[]>([]);
    const [totalDePaginas, setTotalDePaginas] = useState(1);

    useEffect(() => {
        buscarOfertas();
        //eslint-disable-next-line react-hooks/exhaustive-deps
    }, [])

    function buscarOfertas() {
        axios.get(`${urlViajes}/all`)
            .then((respuesta: AxiosResponse<viajeDescuentoDTO[]>) => {
                setOfertas(respuesta.data);
                console.log(respuesta.data);
            })
    }

    return (
        <div id="ofertas" className="my-3">
            <h2 className="text-center">Ofertas</h2>
            <div id="carouselExampleCaptions" className="carousel slide" data-bs-ride="carousel">
                <div className="carousel-indicators">
                    <button type="button" data-bs-target="#carouselExampleCaptions" data-bs-slide-to="0" className="active" aria-current="true" aria-label="Slide 1"></button>
                </div>
                <div className="carousel-inner">
                    {ofertas.map((o) => (
                        <div className="carousel-item" key={o.id+"-oferta"}>
                            <img src="https://images.unsplash.com/photo-1647804212597-15bc7c55c257?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxlZGl0b3JpYWwtZmVlZHwyfHx8ZW58MHx8fHw%3D&auto=format&fit=crop&w=500&q=60" className="d-block w-100" alt="..." />
                            <div className="carousel-caption d-none d-md-block">
                                <h3>{o.lugar}</h3>
                                <h2>{o.pais}</h2>
                                <p>{o.descripcion}</p>
                                <span>{o.precio}</span>
                            </div>
                        </div>
                    ))}
                </div>
                <button className="carousel-control-prev" type="button" data-bs-target="#carouselExampleCaptions" data-bs-slide="prev">
                    <span className="carousel-control-prev-icon" aria-hidden="true"></span>
                    <span className="visually-hidden">Previous</span>
                </button>
                <button className="carousel-control-next" type="button" data-bs-target="#carouselExampleCaptions" data-bs-slide="next">
                    <span className="carousel-control-next-icon" aria-hidden="true"></span>
                    <span className="visually-hidden">Next</span>
                </button>
            </div>
            {/* <div className="row">
                {viajes.map((s) => (
                    <div className="col-md-3 my-3 h-75 col-12">
                        <div className="card" key={s.id} >
                            <img src={s.foto} className="card-img-top"  alt={s.lugar} width="200" height="200"/>
                            <div className="card-body text-center">
                                <h3>{s.lugar}</h3>
                                <h5>{s.pais}</h5>
                                <p className="card-text">{s.descripcion}</p>
                                
                            </div>
                            <div className="card-footer text-end">
                                <span className="font-weight-bold text-danger">{s.precio}€</span>
                            </div>
                        </div>
                    </div>
                ))}
            </div> */}
        </div>  
    )
}
